    try {
      const user = req.user as any;
      const currentUser = await storage.getUser(user.claims.sub);
      if (currentUser?.role !== 'platform_admin') {
        return res.status(403).json({ message: "Access denied - Platform Admin required" });
      }

      const { insertSystemHealthSchema } = await import("@shared/schema");
      const healthData = insertSystemHealthSchema.parse(req.body);

      const health = await storage.updateSystemHealth(healthData);
      res.json(health);
    } catch (error) {
      console.error("Error updating system health:", error);
      res.status(500).json({ message: "Failed to update system health" });
    }
  });

  // Password Reset Management (Platform Admin only)
  app.post("/api/admin/password-reset", isAuthenticated, async (req: any, res) => {
    try {
      const user = req.user as any;
      const currentUser = await storage.getUser(user.claims.sub);
      if (currentUser?.role !== 'platform_admin') {
        return res.status(403).json({ message: "Access denied - Platform Admin required" });
      }

      const { userId } = req.body;
      const targetUser = await storage.getUser(userId);
      if (!targetUser) {
        return res.status(404).json({ message: "User not found" });
      }

      // Generate password reset token
      const { randomUUID } = require('crypto');
      const token = randomUUID();
      const expiresAt = new Date(Date.now() + 24 * 60 * 60 * 1000); // 24 hours

      const { insertPasswordResetTokenSchema } = await import("@shared/schema");
      const tokenData = insertPasswordResetTokenSchema.parse({
        userId,
        token,
        expiresAt,
        createdBy: user.claims.sub,
      });

      const resetToken = await storage.createPasswordResetToken(tokenData);

      // TODO: Send reset token via email
      console.log(`Password reset token generated for ${targetUser.email}: ${token}`);

      res.json({ success: true, message: "Password reset token generated" });
    } catch (error) {
      console.error("Error generating password reset token:", error);
      res.status(500).json({ message: "Failed to generate password reset token" });
    }
  });

  // Discount code management routes (Platform Admin only)
  app.get('/api/platform/discount-codes', isAuthenticated, async (req: any, res) => {
    try {
      const user = await storage.getUser(req.user.claims.sub);
      if (!user || user.role !== 'platform_admin') {
        return res.status(403).json({ message: 'Platform admin access required' });
      }

      const codes = await storage.getDiscountCodes();
      res.json(codes);
    } catch (error) {
      console.error("Error fetching discount codes:", error);
      res.status(500).json({ message: "Failed to fetch discount codes" });
    }
  });

  app.post('/api/platform/discount-codes', isAuthenticated, async (req: any, res) => {
    try {
      const user = await storage.getUser(req.user.claims.sub);
      if (!user || user.role !== 'platform_admin') {
        return res.status(403).json({ message: 'Platform admin access required' });
      }

      const codeData = {
        ...req.body,
        createdBy: user.id,
      };

      const code = await storage.createDiscountCode(codeData);
      res.status(201).json(code);
    } catch (error) {
      console.error("Error creating discount code:", error);
      res.status(500).json({ message: "Failed to create discount code" });
    }
  });

  app.patch('/api/platform/discount-codes/:id', isAuthenticated, async (req: any, res) => {
    try {
      const user = await storage.getUser(req.user.claims.sub);
      if (!user || user.role !== 'platform_admin') {
        return res.status(403).json({ message: 'Platform admin access required' });
      }

      const { id } = req.params;
      const code = await storage.updateDiscountCode(id, req.body);
      res.json(code);
    } catch (error) {
      console.error("Error updating discount code:", error);
      res.status(500).json({ message: "Failed to update discount code" });
    }
  });

  app.get('/api/platform/discount-codes/:id/usage', isAuthenticated, async (req: any, res) => {
    try {
      const user = await storage.getUser(req.user.claims.sub);
      if (!user || user.role !== 'platform_admin') {
        return res.status(403).json({ message: 'Platform admin access required' });
      }

      const { id } = req.params;
      const usage = await storage.getDiscountUsagesByCode(id);
      res.json(usage);
    } catch (error) {
      console.error("Error fetching discount usage:", error);
      res.status(500).json({ message: "Failed to fetch discount usage" });
    }
  });

  // Discount code validation (public for checkout)
  app.post('/api/validate-discount', async (req, res) => {
    try {
      const { code, userId } = req.body;

      if (!code) {
        return res.status(400).json({ message: 'Discount code is required' });
      }

      const validation = await storage.validateDiscountCode(code, userId);
      res.json(validation);
    } catch (error) {
      console.error("Error validating discount code:", error);
      res.status(500).json({ message: "Failed to validate discount code" });
    }
  });

  // Apply discount code during checkout
  app.post('/api/apply-discount', isAuthenticated, async (req: any, res) => {
    try {
      const { discountCodeId, orderValue, stripeInvoiceId } = req.body;
      const userId = req.user.claims.sub;
      const user = await storage.getUser(userId);

      const discountCode = await storage.getDiscountCodeById(discountCodeId);
      if (!discountCode) {
        return res.status(404).json({ message: 'Discount code not found' });
      }

      // Calculate discount amount
      let discountAmount = 0;
      if (discountCode.discountType === 'percentage') {
        discountAmount = Math.round((orderValue * discountCode.discountValue) / 100);
      } else if (discountCode.discountType === 'fixed_amount') {
        discountAmount = discountCode.discountValue;
      }

      // Record usage
      const usage = await storage.recordDiscountUsage({
        discountCodeId,
        userId,
        tenantId: user?.tenantId || null,
        orderValue,
        discountAmount,
        stripeInvoiceId,
      });

      res.json({ usage, discountAmount });
    } catch (error) {
      console.error("Error applying discount:", error);
      res.status(500).json({ message: "Failed to apply discount" });
    }
  });

  // Referral system routes
  app.get('/api/user/referrals', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const referrals = await storage.getReferralsByUser(userId);
      const stats = await storage.getReferralStats(userId);
      const rewards = await storage.getReferralRewardsByUser(userId);

      res.json({ referrals, stats, rewards });
    } catch (error) {
      console.error("Error fetching user referrals:", error);
      res.status(500).json({ message: "Failed to fetch referrals" });
    }
  });

  app.post('/api/user/referrals', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const { referredEmail, campaignName } = req.body;

      // Generate unique referral code
      const referralCode = await storage.generateReferralCode(userId);

      const referral = await storage.createReferral({
        referrerUserId: userId,
        referredEmail,
        referralCode,
        campaignName: campaignName || 'default',
        rewardType: 'discount',
        rewardValue: 2000, // $20 in cents
      });

      res.status(201).json(referral);
    } catch (error) {
      console.error("Error creating referral:", error);
      res.status(500).json({ message: "Failed to create referral" });
    }
  });

  // Complete referral when referred user signs up
  app.post('/api/complete-referral', async (req, res) => {
    try {
      const { referralCode, referredUserId } = req.body;

      if (!referralCode || !referredUserId) {
        return res.status(400).json({ message: 'Referral code and referred user ID are required' });
      }

      const referral = await storage.completeReferral(referralCode, referredUserId);

      // Create rewards for both referrer and referred user
      const referrerReward = await storage.createReferralReward({
        referralId: referral.id,
        userId: referral.referrerUserId,
        rewardType: 'referrer_bonus',
        rewardValue: referral.rewardValue || 2000,
        rewardDescription: 'Referral bonus - friend signed up',
        expiresAt: new Date(Date.now() + 365 * 24 * 60 * 60 * 1000), // 1 year
      });

      const referredReward = await storage.createReferralReward({
        referralId: referral.id,
        userId: referredUserId,
        rewardType: 'referee_discount',
        rewardValue: 1000, // $10 discount for new user
        rewardDescription: 'Welcome discount from referral',
        expiresAt: new Date(Date.now() + 90 * 24 * 60 * 60 * 1000), // 90 days
      });

      res.json({ referral, referrerReward, referredReward });
    } catch (error) {
      console.error("Error completing referral:", error);
      res.status(500).json({ message: "Failed to complete referral" });
    }
  });

  // Apply referral reward to invoice
  app.post('/api/apply-referral-reward', isAuthenticated, async (req: any, res) => {
    try {
      const { rewardId, stripeInvoiceId } = req.body;
      const userId = req.user.claims.sub;

      // Verify the reward belongs to the user
      const rewards = await storage.getReferralRewardsByUser(userId);
      const reward = rewards.find(r => r.id === rewardId);

      if (!reward) {
        return res.status(404).json({ message: 'Reward not found' });
      }

      if (reward.appliedAt) {
        return res.status(400).json({ message: 'Reward already applied' });
      }

      const updatedReward = await storage.markReferralRewardApplied(rewardId, stripeInvoiceId);
      res.json(updatedReward);
    } catch (error) {
      console.error("Error applying referral reward:", error);
      res.status(500).json({ message: "Failed to apply referral reward" });
    }
  });

  // Platform admin - view all referrals
  app.get('/api/platform/referrals', isAuthenticated, async (req: any, res) => {
    try {
      const user = await storage.getUser(req.user.claims.sub);
      if (!user || user.role !== 'platform_admin') {
        return res.status(403).json({ message: 'Platform admin access required' });
      }

      // This would need a new storage method to get all referrals
      // For now, return empty array as placeholder
      res.json([]);
    } catch (error) {
      console.error("Error fetching platform referrals:", error);
      res.status(500).json({ message: "Failed to fetch platform referrals" });
    }
  });

  // Subscription tier information endpoint
  app.get("/api/subscription/tier-info", isAuthenticated, async (req: any, res) => {
    try {
      const user = req.user as any;
      const userId = user?.claims?.sub;
      if (!userId) {
        return res.status(401).json({ message: "Invalid user session" });
      }

      const currentUser = await storage.getUser(userId);
      if (!currentUser || !currentUser.tenantId) {
        return res.status(404).json({ message: "User or tenant not found" });
      }

      const tenant = await storage.getTenant(currentUser.tenantId);
      if (!tenant) {
        return res.status(404).json({ message: "Tenant not found" });
      }

      // Import the tier info function
      const { getTierInfo } = await import('./subscriptionFeatures');
      const tierInfo = getTierInfo(tenant.subscriptionTier);

      res.json(tierInfo);
    } catch (error) {
      console.error("Error fetching tier info:", error);
      res.status(500).json({ message: "Failed to fetch subscription tier information" });
    }
  });

  // Website customization endpoints
  app.get("/api/website-settings/:tenantId", isAuthenticated, validateParam('tenantId'), async (req: any, res) => {
    try {
      const { tenantId } = req.params;

      // Validate tenant access
      const user = req.user as any;
      const userId = user?.claims?.sub;
      if (!userId) {
        return res.status(401).json({ message: "Invalid user session" });
      }

      const validationResult = await validateTenantAccess(userId, tenantId, storage);
      if (!validationResult.valid) {
        return res.status(403).json({ message: validationResult.error });
      }

      const settings = await storage.getWebsiteSettings(tenantId);
      res.json(settings || {});
    } catch (error) {
      console.error("Error fetching website settings:", error);
      res.status(500).json({ message: "Failed to fetch website settings" });
    }
  });

  app.post("/api/website-settings", isAuthenticated, async (req: any, res) => {
    try {
      const user = req.user as any;
      const userId = user?.claims?.sub;
      if (!userId) {
        return res.status(401).json({ message: "Invalid user session" });
      }

      const currentUser = await storage.getUser(userId);
      if (!currentUser?.tenantId) {
        return res.status(404).json({ message: "User or tenant not found" });
      }

      // Only allow tenant admins and platform admins to modify website settings
      if (currentUser.role !== 'tenant_admin' && currentUser.role !== 'platform_admin') {
        return res.status(403).json({ message: "Only administrators can modify website settings" });
      }

      const settingsData = {
        tenantId: currentUser.tenantId,
        ...req.body
      };

      const settings = await storage.upsertWebsiteSettings(settingsData);
      res.json(settings);
    } catch (error) {
      console.error("Error updating website settings:", error);
      res.status(500).json({ message: "Failed to update website settings" });
    }
  });

  app.patch("/api/website-settings/:tenantId", isAuthenticated, validateParam('tenantId'), async (req: any, res) => {
    try {
      const { tenantId } = req.params;

      // Validate tenant access
      const user = req.user as any;
      const userId = user?.claims?.sub;
      if (!userId) {
        return res.status(401).json({ message: "Invalid user session" });
      }

      const validationResult = await validateTenantAccess(userId, tenantId, storage);
      if (!validationResult.valid) {
        return res.status(403).json({ message: validationResult.error });
      }

      // Only allow tenant admins and platform admins to modify website settings
      const currentUser = await storage.getUser(userId);
      if (currentUser?.role !== 'tenant_admin' && currentUser?.role !== 'platform_admin') {
        return res.status(403).json({ message: "Only administrators can modify website settings" });
      }

      const settings = await storage.updateWebsiteSettings(tenantId, req.body);
      res.json(settings);
    } catch (error) {
      console.error("Error updating website settings:", error);
      res.status(500).json({ message: "Failed to update website settings" });
    }
  });

  return httpServer;
}

server/storage.ts.content:
import {
  users,
  tenants,
  employees,
  departments,
  jobPositions,
  feedbacks,
  goals,
  performanceReviews,
  notifications,
  notificationPreferences,
  pricingTiers,
  billingAuditLog,
  systemSettings,
  supportTickets,
  supportIntegrations,
  knowledgeBase,
  chatSessions,
  chatMessages,
  systemHealth,
  passwordResetTokens,
  discountCodes,
  discountCodeUsages,
  referrals,
  referralRewards,
  websiteSettings,
  type User,
  type UpsertUser,
  type Tenant,
  type Employee,
  type Department,
  type JobPosition,
  type Feedback,
  type Goal,
  type InsertTenant,
  type InsertEmployee,
  type InsertFeedback,
  type InsertGoal,
  type PerformanceReview,
  type InsertPerformanceReview,
  type Notification,
  type NotificationPreferences,
  type InsertNotification,
  type InsertNotificationPreferences,
  type PricingTier,
  type InsertPricingTier,
  type BillingAuditLog,
  type InsertBillingAuditLog,
  type SystemSetting,
  type InsertSystemSetting,
  type SupportTicket,
  type InsertSupportTicket,
  type SupportIntegration,
  type InsertSupportIntegration,
  type KnowledgeBase,
  type InsertKnowledgeBase,
  type ChatSession,
  type InsertChatSession,
  type ChatMessage,
  type InsertChatMessage,
  type SystemHealth,
  type InsertSystemHealth,
  type PasswordResetToken,
  type InsertPasswordResetToken,
  type DiscountCode,
  type InsertDiscountCode,
  type DiscountCodeUsage,
  type InsertDiscountCodeUsage,
  type Referral,
  type InsertReferral,
  type ReferralReward,
  type InsertReferralReward,
  type InsertDepartment,
  type InsertJobPosition,
  type WebsiteSettings,
  type InsertWebsiteSettings,
} from "@shared/schema";
import { db } from "./db";
import { eq, and, desc, count, sql, lt } from "drizzle-orm";
import { randomUUID } from "crypto";

export interface IStorage {
  // User operations (mandatory for Replit Auth)
  getUser(id: string): Promise<User | undefined>;
  getAllUsers(): Promise<User[]>;
  upsertUser(user: UpsertUser): Promise<User>;
  updateUser(id: string, data: Partial<UpsertUser>): Promise<User>;
  deleteUser(id: string): Promise<void>;
  createUser(userData: UpsertUser): Promise<User>;

  // Tenant operations
  getTenant(id: string): Promise<Tenant | undefined>;
  createTenant(tenant: InsertTenant): Promise<Tenant>;
  updateTenant(id: string, data: Partial<InsertTenant>): Promise<Tenant>;
  deleteTenant(id: string): Promise<void>;

  // Employee operations
  getEmployee(id: string): Promise<Employee | undefined>;
  getEmployeeByUserId(userId: string): Promise<Employee | undefined>;
  getEmployeeByFeedbackUrl(feedbackUrl: string): Promise<Employee | undefined>;
  getEmployeesByTenant(tenantId: string): Promise<Employee[]>;
  getEmployeesByDepartment(departmentId: string): Promise<Employee[]>;
  getEmployeesByManager(managerId: string): Promise<Employee[]>;
  createEmployee(employee: InsertEmployee): Promise<Employee>;
  updateEmployee(id: string, data: Partial<InsertEmployee>): Promise<Employee>;
  bulkUpdateEmployees(updates: {id: string, data: Partial<InsertEmployee>}[]): Promise<Employee[]>;
  bulkAssignDepartment(employeeIds: string[], departmentId: string): Promise<Employee[]>;
  bulkAssignManager(employeeIds: string[], managerId: string): Promise<Employee[]>;
  getEmployeeHierarchy(employeeId: string): Promise<any>;
  searchEmployees(tenantId: string, query: string, filters?: any): Promise<Employee[]>;
  createEmployeeWithLimitCheck(employee: InsertEmployee, maxEmployees: number): Promise<Employee>;

  // Department operations
  getDepartmentsByTenant(tenantId: string): Promise<Department[]>;
  createDepartment(department: InsertDepartment): Promise<Department>;
  updateDepartment(id: string, data: Partial<InsertDepartment>): Promise<Department>;
  deleteDepartment(id: string): Promise<void>;
  getDepartment(id: string): Promise<Department | undefined>;

  // Job Position operations
  getJobPositionsByTenant(tenantId: string): Promise<JobPosition[]>;
  createJobPosition(jobPosition: InsertJobPosition): Promise<JobPosition>;
  updateJobPosition(id: string, data: Partial<InsertJobPosition>): Promise<JobPosition>;
  deleteJobPosition(id: string): Promise<void>;
  getJobPosition(id: string): Promise<JobPosition | undefined>;

  // Feedback operations
  createFeedback(feedback: InsertFeedback): Promise<Feedback>;
  getFeedbacksByEmployee(employeeId: string): Promise<Feedback[]>;

  // Goal operations
  createGoal(goal: InsertGoal): Promise<Goal>;
  getGoalsByEmployee(employeeId: string): Promise<Goal[]>;

  // Performance Review operations
  createPerformanceReview(review: InsertPerformanceReview): Promise<PerformanceReview>;
  getPerformanceReview(id: string): Promise<PerformanceReview | undefined>;
  getPerformanceReviewsByEmployee(employeeId: string): Promise<PerformanceReview[]>;
  getPerformanceReviewsByTenant(tenantId: string): Promise<PerformanceReview[]>;
  updatePerformanceReview(id: string, data: Partial<InsertPerformanceReview>): Promise<PerformanceReview>;
  deletePerformanceReview(id: string): Promise<void>;

  // Dashboard analytics
  getDashboardMetrics(tenantId: string): Promise<{
    totalEmployees: number;
    totalFeedback: number;
    avgPerformance: number;
    activeReviews: number;
  }>;

  // Platform analytics for Platform Super Admins
  getPlatformMetrics(): Promise<{
    totalTenants: number;
    totalUsers: number;
    totalEmployees: number;
    totalFeedback: number;
    activeSubscriptions: number;
    monthlyRecurringRevenue: number;
  }>;

  // Platform tenant management
  getAllUsersWithTenants(): Promise<(User & { tenantName?: string | null })[]>;
  getAllTenants(): Promise<Tenant[]>;

  // Recent activity
  getRecentActivity(tenantId: string): Promise<any[]>;

  // Notification operations
  createNotification(notification: InsertNotification): Promise<Notification>;
  getNotificationsByUser(userId: string): Promise<Notification[]>;
  markNotificationAsRead(id: string): Promise<void>;
  deleteNotification(id: string): Promise<void>;

  // Notification preferences operations
  getUserNotificationPreferences(userId: string): Promise<NotificationPreferences | undefined>;
  upsertNotificationPreferences(preferences: InsertNotificationPreferences): Promise<NotificationPreferences>;

  // Pricing tier management operations
  getPricingTiers(): Promise<PricingTier[]>;
  getPricingTier(id: string): Promise<PricingTier | undefined>;
  createPricingTier(tier: InsertPricingTier): Promise<PricingTier>;
  updatePricingTier(id: string, data: Partial<InsertPricingTier>): Promise<PricingTier>;
  deletePricingTier(id: string): Promise<void>;

  // Billing audit log operations
  createBillingAuditLog(log: InsertBillingAuditLog): Promise<BillingAuditLog>;
  getBillingAuditLog(tenantId?: string, limit?: number): Promise<BillingAuditLog[]>;

  // Tenant billing operations
  changeTenantTier(tenantId: string, newTierId: string, userId: string): Promise<Tenant>;

  // System settings operations
  getSystemSettings(): Promise<SystemSetting[]>;
  getSystemSetting(key: string): Promise<SystemSetting | undefined>;
  getSystemSettingsByCategory(category: string): Promise<SystemSetting[]>;
  upsertSystemSetting(setting: InsertSystemSetting): Promise<SystemSetting>;
  updateSystemSetting(key: string, value: any, userId: string): Promise<SystemSetting>;
  deleteSystemSetting(key: string): Promise<void>;

  // Support ticket operations
  createSupportTicket(ticket: InsertSupportTicket): Promise<SupportTicket>;
  getSupportTickets(tenantId?: string): Promise<SupportTicket[]>;
  getSupportTicket(id: string): Promise<SupportTicket | undefined>;
  updateSupportTicket(id: string, data: Partial<InsertSupportTicket>): Promise<SupportTicket>;

  // Support integration operations
  createSupportIntegration(integration: InsertSupportIntegration): Promise<SupportIntegration>;
  getSupportIntegrations(): Promise<SupportIntegration[]>;
  updateSupportIntegration(id: string, data: Partial<InsertSupportIntegration>): Promise<SupportIntegration>;

  // Knowledge base operations
  createKnowledgeBaseArticle(article: InsertKnowledgeBase): Promise<KnowledgeBase>;
  getKnowledgeBaseArticles(category?: string): Promise<KnowledgeBase[]>;
  getKnowledgeBaseArticle(slug: string): Promise<KnowledgeBase | undefined>;
  incrementKnowledgeBaseViews(id: string): Promise<void>;

  // Live chat operations
  createChatSession(session: InsertChatSession): Promise<ChatSession>;
  getChatSession(id: string): Promise<ChatSession | undefined>;
  updateChatSession(id: string, data: Partial<InsertChatSession>): Promise<ChatSession>;
  createChatMessage(message: InsertChatMessage): Promise<ChatMessage>;
  getChatMessages(sessionId: string): Promise<ChatMessage[]>;

  // System health operations
  updateSystemHealth(health: InsertSystemHealth): Promise<SystemHealth>;
  getSystemHealthStatus(): Promise<SystemHealth[]>;

  // Password reset operations
  createPasswordResetToken(token: InsertPasswordResetToken): Promise<PasswordResetToken>;
  getPasswordResetToken(token: string): Promise<PasswordResetToken | undefined>;
  markPasswordResetTokenUsed(token: string): Promise<void>;
  deleteExpiredPasswordResetTokens(): Promise<void>;

  // Website customization operations
  getWebsiteSettings(tenantId: string): Promise<WebsiteSettings | undefined>;
  upsertWebsiteSettings(settings: InsertWebsiteSettings): Promise<WebsiteSettings>;
  updateWebsiteSettings(tenantId: string, data: Partial<InsertWebsiteSettings>): Promise<WebsiteSettings>;
}

export class DatabaseStorage implements IStorage {
  // User operations
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user;
  }

  async getAllUsers(): Promise<User[]> {
    return await db.select().from(users);
  }

  async upsertUser(userData: UpsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(userData)
      .onConflictDoUpdate({
        target: users.id,
        set: {
          ...userData,
          updatedAt: new Date(),
        },
      })
      .returning();
    return user;
  }

  async updateUser(id: string, data: Partial<UpsertUser>): Promise<User> {
    const [user] = await db
      .update(users)
      .set({ ...data, updatedAt: new Date() })
      .where(eq(users.id, id))
      .returning();
    return user;
  }

  async deleteUser(id: string): Promise<void> {
    const result = await db.delete(users).where(eq(users.id, id));
    if (result.rowCount === 0) {
      throw new Error(`User with id ${id} not found or could not be deleted`);
    }
  }

  async createUser(userData: UpsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(userData)
      .returning();
    return user;
  }

  // Tenant operations
  async getTenant(id: string): Promise<Tenant | undefined> {
    const [tenant] = await db.select().from(tenants).where(eq(tenants.id, id));
    return tenant;
  }

  async createTenant(tenantData: InsertTenant): Promise<Tenant> {
    const [tenant] = await db
      .insert(tenants)
      .values(tenantData)
      .returning();
    return tenant;
  }

  async updateTenant(id: string, data: Partial<InsertTenant>): Promise<Tenant> {
    const [tenant] = await db
      .update(tenants)
      .set({ ...data, updatedAt: new Date() })
      .where(eq(tenants.id, id))
      .returning();
    return tenant;
  }

  async deleteTenant(id: string): Promise<void> {
    try {
      const result = await db.delete(tenants).where(eq(tenants.id, id));
      if (result.rowCount === 0) {
        throw new Error(`Tenant with id ${id} not found or could not be deleted`);
      }
    } catch (error: any) {
      if (error.code === '23503') { // Foreign key constraint violation
        throw new Error('Cannot delete tenant: There are associated records (employees, departments, etc.). Please remove all associated records first.');
      }
      throw error;
    }
  }

