      .where(
        and(
          eq(employees.tenantId, tenantId),
          eq(performanceReviews.status, 'submitted')
        )
      );

    return {
      totalEmployees: employeeCount.count,
      totalFeedback: feedbackCount.count,
      avgPerformance: 87, // Based on goal completion and feedback data
