@import url('https://fonts.googleapis.com/css2?family=Inter:wght@300;400;500;600;700&display=swap');
@tailwind base;
@tailwind components;
@tailwind utilities;

:root {
  /* Consistent Light Theme - Professional SaaS Colors */
  --background: hsl(210, 40%, 98%);
  --foreground: hsl(215, 25%, 27%);
  --card: hsl(0, 0%, 100%);
  --card-foreground: hsl(215, 25%, 27%);
  --popover: hsl(0, 0%, 100%);
  --popover-foreground: hsl(215, 25%, 27%);
  --primary: hsl(221, 83%, 53%);
  --primary-foreground: hsl(0, 0%, 100%);
  --secondary: hsl(210, 40%, 95%);
  --secondary-foreground: hsl(215, 25%, 27%);
  --muted: hsl(210, 40%, 96%);
  --muted-foreground: hsl(215, 13%, 65%);
  --accent: hsl(210, 40%, 95%);
  --accent-foreground: hsl(215, 25%, 27%);
  --destructive: hsl(0, 72%, 51%);
  --destructive-foreground: hsl(0, 0%, 100%);
  --border: hsl(214, 32%, 91%);
  --input: hsl(214, 32%, 91%);
  --ring: hsl(221, 83%, 53%);
  --chart-1: hsl(221, 83%, 53%);
  --chart-2: hsl(142, 76%, 36%);
  --chart-3: hsl(38, 92%, 50%);
  --chart-4: hsl(268, 83%, 58%);
  --chart-5: hsl(340, 75%, 55%);
  /* Sidebar - Consistent with main theme */
  --sidebar: hsl(0, 0%, 100%);
  --sidebar-foreground: hsl(215, 25%, 27%);
  --sidebar-primary: hsl(221, 83%, 53%);
  --sidebar-primary-foreground: hsl(0, 0%, 100%);
  --sidebar-accent: hsl(210, 40%, 95%);
  --sidebar-accent-foreground: hsl(215, 25%, 27%);
  --sidebar-border: hsl(214, 32%, 91%);
  --sidebar-ring: hsl(221, 83%, 53%);
  /* Typography */
  --font-sans: 'Inter', sans-serif;
  --font-serif: 'Inter', sans-serif;
  --font-mono: 'JetBrains Mono', monospace;
  --radius: 0.5rem;
  /* Consistent shadows */
  --shadow-2xs: 0px 1px 2px 0px rgba(0, 0, 0, 0.05);
  --shadow-xs: 0px 1px 2px 0px rgba(0, 0, 0, 0.05);
  --shadow-sm: 0px 1px 2px 0px rgba(0, 0, 0, 0.05), 0px 1px 3px 0px rgba(0, 0, 0, 0.10);
  --shadow: 0px 1px 3px 0px rgba(0, 0, 0, 0.10), 0px 1px 2px 0px rgba(0, 0, 0, 0.06);
  --shadow-md: 0px 4px 6px -1px rgba(0, 0, 0, 0.10), 0px 2px 4px -1px rgba(0, 0, 0, 0.06);
  --shadow-lg: 0px 10px 15px -3px rgba(0, 0, 0, 0.10), 0px 4px 6px -2px rgba(0, 0, 0, 0.05);
  --shadow-xl: 0px 20px 25px -5px rgba(0, 0, 0, 0.10), 0px 10px 10px -5px rgba(0, 0, 0, 0.04);
  --shadow-2xl: 0px 25px 50px -12px rgba(0, 0, 0, 0.25);
  --tracking-normal: 0em;
  --spacing: 0.25rem;
}


@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased text-foreground;
    background: linear-gradient(135deg, hsl(210, 40%, 98%) 0%, hsl(220, 60%, 99%) 100%);
    min-height: 100vh;
  }
}

@layer components {
  .glass-morphism {
    background: rgba(255, 255, 255, 0.80);
    backdrop-filter: blur(20px);
    -webkit-backdrop-filter: blur(20px);
    border: 1px solid var(--border);
    box-shadow: var(--shadow-lg);
  }

  .glass-card {
    background: var(--card);
    border: 1px solid var(--border);
    box-shadow: var(--shadow-md);
  }

  .sidebar-item {
    transition: all 0.2s ease;
    border-radius: var(--radius);
  }

  .sidebar-item:hover {
    transform: translateX(2px);
    background: var(--sidebar-accent);
  }

  .sidebar-item.active {
    background: var(--sidebar-accent);
    border-right: 3px solid var(--sidebar-primary);
    color: var(--sidebar-primary);
  }

  .performance-ring {
    background: conic-gradient(from 0deg, var(--primary) 0%, var(--primary) 87%, var(--muted) 87%, var(--muted) 100%);
  }

  .gradient-text {
    background: linear-gradient(135deg, var(--primary) 0%, hsl(268, 83%, 58%) 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
  }

  .animate-fade-in {
    animation: fadeIn 0.5s ease-in-out;
  }

  @keyframes fadeIn {
    from { opacity: 0; transform: translateY(10px); }
    to { opacity: 1; transform: translateY(0); }
  }

  /* Extra small breakpoint for button text visibility - 480px+ */
  @media (min-width: 480px) {
    .hidden.xs\:inline {
      display: inline !important;
    }
  }

  /* Add padding and spacing adjustments for better mobile experience */
  @media (max-width: 640px) {
    .glass-card {
      padding: 1rem;
    }

    /* Ensure better spacing on mobile */
    .space-y-6 > * + * {
      margin-top: 1.5rem;
    }

    .space-y-3 > * + * {
      margin-top: 0.75rem;
    }

    /* Make sure truncated text works properly */
    .truncate {
      white-space: nowrap;
      overflow: hidden;
      text-overflow: ellipsis;
    }

    /* Bento grid adjustments for mobile */
    .grid.grid-cols-1.md\:grid-cols-2.lg\:grid-cols-4 {
      grid-template-columns: 1fr;
      gap: 1rem;
    }

    /* Reset span classes on mobile */
    .lg\:col-span-2 {
      grid-column: span 1;
    }

    .lg\:row-span-2 {
      grid-row: span 1;
    }
  }

  /* Medium screen adjustments */
  @media (min-width: 641px) and (max-width: 1023px) {
    .md\:grid-cols-2.lg\:grid-cols-4 {
      grid-template-columns: repeat(2, 1fr);
    }

    .lg\:col-span-2 {
      grid-column: span 2;
    }
  }
}

