import { useState } from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Badge } from '@/components/ui/badge';
import { Avatar, AvatarFallback, AvatarImage } from '@/components/ui/avatar';
import { Skeleton } from '@/components/ui/skeleton';
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from '@/components/ui/table';
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from '@/components/ui/dialog';
import { 
  Plus, 
  Search, 
  MoreHorizontal, 
  Edit, 
  Trash2, 
  Mail,
  Phone
} from 'lucide-react';
import { apiRequest } from '@/lib/queryClient';
import { useToast } from '@/hooks/use-toast';

interface EmployeeManagementProps {
  user: any;
}

export default function EmployeeManagement({ user }: EmployeeManagementProps) {
  const [searchTerm, setSearchTerm] = useState('');
  const [isAddEmployeeOpen, setIsAddEmployeeOpen] = useState(false);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Check if user can add employees (only tenant_admin and manager)
  const canAddEmployees = user?.role === 'tenant_admin' || user?.role === 'manager';

  const { data: employees, isLoading } = useQuery({
    queryKey: ['/api/employees', user.tenant?.id],
    enabled: !!user.tenant?.id,
  });

  const addEmployeeMutation = useMutation({
    mutationFn: async (employeeData: any) => {
      const response = await apiRequest('POST', '/api/employees', employeeData);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/employees'] });
      toast({
        title: "Success",
        description: "Employee added successfully",
      });
      setIsAddEmployeeOpen(false);
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: "Failed to add employee",
        variant: "destructive",
      });
    },
  });

  const filteredEmployees = (employees as any[])?.filter((emp: any) =>
    emp.user?.firstName?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    emp.user?.lastName?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    emp.user?.email?.toLowerCase().includes(searchTerm.toLowerCase())
  ) || [];

  return (
    <div className="space-y-6" data-testid="employee-management-container">
      {/* Header Actions */}
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-2xl font-bold" data-testid="heading-employee-management">Employee Management</h2>
          <p className="text-muted-foreground">Manage your team members and their roles</p>
        </div>
        {canAddEmployees && (
          <Dialog open={isAddEmployeeOpen} onOpenChange={setIsAddEmployeeOpen}>
            <DialogTrigger asChild>
              <Button className="bg-gradient-to-r from-blue-500 to-blue-600" data-testid="button-add-employee">
                <Plus className="w-4 h-4 mr-2" />
                Add Employee
              </Button>
            </DialogTrigger>
          <DialogContent data-testid="dialog-add-employee">
            <DialogHeader>
              <DialogTitle>Add New Employee</DialogTitle>
              <DialogDescription>
                Create a new employee profile and assign their role in the organization.
              </DialogDescription>
            </DialogHeader>
            {/* Add employee form would go here */}
            <DialogFooter>
              <Button variant="outline" onClick={() => setIsAddEmployeeOpen(false)} data-testid="button-cancel-add-employee">
                Cancel
              </Button>
              <Button 
                onClick={() => {
                  // TODO: Implement actual employee creation
                  toast({
                    title: "Feature Coming Soon",
                    description: "Employee creation form will be implemented",
                  });
                }}
                data-testid="button-save-employee"
              >
                Save Employee
              </Button>
            </DialogFooter>
          </DialogContent>
        </Dialog>
        )}
      </div>

      {/* Search and Filters */}
      <Card className="glass-card border-0" data-testid="card-employee-search">
        <CardContent className="p-6">
          <div className="flex items-center space-x-4">
            <div className="relative flex-1">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-muted-foreground" />
              <Input
                placeholder="Search employees..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="pl-10"
                data-testid="input-search-employees"
              />
            </div>
            <Button variant="outline" data-testid="button-filter-employees">
              Filter
            </Button>
          </div>
        </CardContent>
      </Card>

      {/* Employee Table */}
      <Card className="glass-card border-0" data-testid="card-employee-table">
        <CardHeader>
          <div className="flex items-center justify-between">
            <CardTitle>Team Members</CardTitle>
            <Badge variant="outline" data-testid="badge-employee-count">
              {(employees as any[])?.length || 0} employees
            </Badge>
          </div>
        </CardHeader>
        <CardContent>
          {isLoading ? (
            <div className="space-y-4">
              {Array.from({ length: 5 }).map((_, i) => (
                <div key={i} className="flex items-center space-x-4" data-testid={`skeleton-employee-${i}`}>
                  <Skeleton className="h-12 w-12 rounded-full" />
                  <div className="space-y-2 flex-1">
                    <Skeleton className="h-4 w-48" />
                    <Skeleton className="h-3 w-32" />
                  </div>
                  <Skeleton className="h-8 w-20" />
                </div>
              ))}
            </div>
          ) : (
            <Table>
              <TableHeader>
                <TableRow>
                  <TableHead>Employee</TableHead>
                  <TableHead>Position</TableHead>
                  <TableHead>Department</TableHead>
                  <TableHead>Performance</TableHead>
                  <TableHead>Status</TableHead>
                  <TableHead>Actions</TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {filteredEmployees?.length === 0 ? (
                  <TableRow>
                    <TableCell colSpan={6} className="text-center py-8">
                      <div className="text-muted-foreground" data-testid="text-no-employees">
                        No employees found. Add your first team member to get started.
                      </div>
                    </TableCell>
                  </TableRow>
                ) : (
                  filteredEmployees?.map((employee: any, index: number) => (
                    <TableRow key={employee.id} data-testid={`row-employee-${index}`}>
                      <TableCell>
                        <div className="flex items-center space-x-3">
                          <Avatar className="w-10 h-10">
                            <AvatarImage 
                              src={employee.user?.profileImageUrl} 
                              alt={`${employee.user?.firstName} ${employee.user?.lastName}`}
                              className="object-cover"
                            />
                            <AvatarFallback>
                              {employee.user?.firstName?.[0]}{employee.user?.lastName?.[0]}
                            </AvatarFallback>
                          </Avatar>
                          <div>
                            <p className="font-medium" data-testid={`text-employee-name-${index}`}>
                              {employee.user?.firstName} {employee.user?.lastName}
                            </p>
                            <p className="text-sm text-muted-foreground flex items-center" data-testid={`text-employee-email-${index}`}>
                              <Mail className="w-3 h-3 mr-1" />
                              {employee.user?.email}
                            </p>
                          </div>
                        </div>
                      </TableCell>
                      <TableCell data-testid={`text-employee-position-${index}`}>
                        {employee.jobPosition?.title || 'Not Assigned'}
                      </TableCell>
                      <TableCell data-testid={`text-employee-department-${index}`}>
                        {employee.department?.name || 'Unassigned'}
                      </TableCell>
                      <TableCell>
                        <div className="flex items-center space-x-2">
                          <div className="w-8 h-8 performance-ring rounded-full flex items-center justify-center">
                            <div className="w-6 h-6 bg-white rounded-full flex items-center justify-center">
                              <span className="text-xs font-bold text-primary" data-testid={`text-employee-score-${index}`}>
                                {Math.floor(Math.random() * 30) + 70}
                              </span>
                            </div>
                          </div>
                        </div>
                      </TableCell>
                      <TableCell>
                        <Badge 
                          variant={employee.status === 'active' ? 'default' : 'secondary'}
                          className="capitalize"
                          data-testid={`badge-employee-status-${index}`}
                        >
                          {employee.status || 'active'}
                        </Badge>
                      </TableCell>
                      <TableCell>
                        <Button variant="ghost" size="icon" data-testid={`button-employee-actions-${index}`}>
                          <MoreHorizontal className="w-4 h-4" />
                        </Button>
                      </TableCell>
                    </TableRow>
                  ))
                )}
              </TableBody>
            </Table>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/FeedbackSystem.tsx.content:
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Avatar, AvatarFallback, AvatarImage } from '@/components/ui/avatar';
import { Copy, QrCode, Users } from 'lucide-react';
import { useToast } from '@/hooks/use-toast';
import { useQuery } from '@tanstack/react-query';

interface FeedbackSystemProps {
  user: any;
}

export default function FeedbackSystem({ user }: FeedbackSystemProps) {
  const { toast } = useToast();

  // Get a sample employee from the tenant to showcase the feedback system
  const { data: employees = [], isLoading: employeesLoading } = useQuery({
    queryKey: ['/api/employees', user.tenant?.id],
    enabled: !!user.tenant?.id,
  });

  // Get dashboard metrics for feedback statistics
  const { data: metrics } = useQuery({
    queryKey: ['/api/dashboard/metrics', user.tenant?.id],
    enabled: !!user.tenant?.id,
  });

  const metricsData = (metrics as any) || {};
  const employeesArray = Array.isArray(employees) ? employees : [];

  // Pick the first employee as sample (could be random or most recent)
  const sampleEmployee = employeesArray.length > 0 ? employeesArray[0] : null;

  const handleCopyFeedbackLink = (url: string) => {
    navigator.clipboard.writeText(url);
    toast({
      title: "Link Copied!",
      description: "Feedback link has been copied to clipboard",
    });
  };

  return (
    <Card className="glass-card border-0" data-testid="card-feedback-system">
      <CardHeader>
        <CardTitle>Universal Feedback System</CardTitle>
      </CardHeader>
      <CardContent className="space-y-6">
        {/* Real Employee Feedback Profile */}
        {employeesLoading ? (
          <div className="animate-pulse bg-gray-100 p-4 rounded-xl border">
            <div className="flex items-center space-x-3 mb-3">
              <div className="w-12 h-12 bg-gray-200 rounded-full"></div>
              <div className="space-y-2">
                <div className="h-4 bg-gray-200 rounded w-24"></div>
                <div className="h-3 bg-gray-200 rounded w-20"></div>
              </div>
            </div>
          </div>
        ) : sampleEmployee ? (
          <div className="bg-gradient-to-br from-blue-50 to-indigo-50 p-4 rounded-xl border border-blue-100" data-testid="feedback-profile-sample">
            <div className="flex items-center space-x-3 mb-3">
              <Avatar className="w-12 h-12 border-2 border-white">
                <AvatarImage 
                  src={sampleEmployee.profileImageUrl || `https://api.dicebear.com/7.x/avataaars/svg?seed=${sampleEmployee.id || 'default'}`}
                  alt={`${sampleEmployee.firstName || 'Unknown'} ${sampleEmployee.lastName || 'User'}`}
                  className="object-cover"
                />
                <AvatarFallback>
                  {(sampleEmployee.firstName?.charAt(0) || 'U')}{(sampleEmployee.lastName?.charAt(0) || 'U')}
                </AvatarFallback>
              </Avatar>
              <div>
                <p className="font-semibold text-blue-900" data-testid="text-sample-employee-name">
                  {sampleEmployee.firstName || 'Unknown'} {sampleEmployee.lastName || 'User'}
                </p>
                <p className="text-sm text-blue-700" data-testid="text-sample-employee-role">
                  {sampleEmployee.email?.includes('admin') ? 'Tenant Admin' : 
                   sampleEmployee.email?.includes('manager') ? 'Manager' : 'Team Member'}
                </p>
              </div>
            </div>
            <div className="space-y-3">
              <div>
                <p className="text-xs font-medium text-blue-700 mb-2">Personal Feedback URL</p>
                <div className="flex items-center space-x-2">
                  <code className="flex-1 px-3 py-2 bg-white rounded-lg text-xs font-mono" data-testid="code-feedback-url">
                    {sampleEmployee.feedbackUrl ? `fbk.${user.tenant?.domain || 'company'}.com/${sampleEmployee.feedbackUrl}` : 'Generating URL...'}
                  </code>
                  <Button
                    size="icon"
                    variant="ghost"
                    onClick={() => handleCopyFeedbackLink(sampleEmployee.feedbackUrl ? `fbk.${user.tenant?.domain || 'company'}.com/${sampleEmployee.feedbackUrl}` : '')}
                    className="hover:bg-white/50"
                    data-testid="button-copy-feedback-url"
                    disabled={!sampleEmployee.feedbackUrl}
                  >
                    <Copy className="w-4 h-4 text-blue-600" />
                  </Button>
                </div>
              </div>

              {/* QR Code and Stats */}
              <div className="flex justify-between items-center">
                <div className="text-center">
                  <div className="w-16 h-16 bg-white p-2 rounded-lg border-2 border-blue-200 mb-2" data-testid="qr-code-display">
                    <div className="w-full h-full bg-gradient-to-br from-blue-600 to-blue-800 rounded flex items-center justify-center">
                      <QrCode className="w-6 h-6 text-white" />
                    </div>
                  </div>
                  <p className="text-xs text-blue-700">QR Code</p>
                </div>
                <div className="text-center">
                  <div className="w-12 h-12 bg-gradient-to-br from-green-100 to-green-200 rounded-full flex items-center justify-center mb-2">
                    <span className="text-xl font-bold text-green-600" data-testid="text-performance-score">
                      {metricsData.avgPerformance || 85}
                    </span>
                  </div>
                  <p className="text-xs text-blue-700">Score</p>
                </div>
                <div className="text-center">
                  <div className="w-12 h-12 bg-gradient-to-br from-purple-100 to-purple-200 rounded-full flex items-center justify-center mb-2">
                    <span className="text-xl font-bold text-purple-600" data-testid="text-review-count">
                      {metricsData.totalFeedback || 0}
                    </span>
                  </div>
                  <p className="text-xs text-blue-700">Reviews</p>
                </div>
              </div>
            </div>
          </div>
        ) : (
          <div className="bg-gray-50 p-4 rounded-xl border text-center">
            <Users className="w-12 h-12 text-gray-400 mx-auto mb-2" />
            <p className="text-gray-600">No employees available to showcase</p>
          </div>
        )}

        {/* Real Feedback Stats */}
        <div className="grid grid-cols-2 gap-4">
          <div className="text-center p-4 bg-green-50 rounded-xl border border-green-100" data-testid="stat-total-feedback">
            <p className="text-2xl font-bold text-green-600">{metricsData.totalEmployees || 0}</p>
            <p className="text-sm text-green-700">Active Employees</p>
          </div>
          <div className="text-center p-4 bg-blue-50 rounded-xl border border-blue-100" data-testid="stat-avg-rating">
            <p className="text-2xl font-bold text-blue-600">{((metricsData.avgPerformance || 85) / 100 * 5).toFixed(1)}</p>
            <p className="text-sm text-blue-700">Avg Rating</p>
          </div>
        </div>

        <Button 
          className="w-full bg-gradient-to-r from-indigo-500 to-purple-600 hover:from-indigo-600 hover:to-purple-700 transition-all transform hover:scale-105 font-medium"
          data-testid="button-generate-feedback-links"
        >
          Generate New Feedback Links
        </Button>
      </CardContent>
    </Card>
  );
}

