                                size="sm"
                                variant="outline"
                                onClick={() => loginAsUserMutation.mutate(user.id)}
                                disabled={loginAsUserMutation.isPending}
                                data-testid={`button-login-as-${user.firstName?.toLowerCase()}`}
                                className="flex items-center gap-1"
