    if (!newGoal.title || !newGoal.category || !newGoal.targetDate) {
      toast({
        title: 'Missing Information',
        description: 'Please fill in all required fields.',
        variant: 'destructive'
      });
      return;
    }

    addGoalMutation.mutate(newGoal);
