                  {(!searchQuery && filterPriority === 'all' && filterCategory === 'all') && (
                    <Button onClick={() => setIsNewGoalDialogOpen(true)} className="bg-gradient-to-r from-blue-600 to-purple-600 hover:from-blue-700 hover:to-purple-700 text-white">
