              </Card>
            ) : viewMode === 'calendar' ? (
              <CalendarView goals={filteredGoals} />
            ) : (
              <div className="grid grid-cols-1 lg:grid-cols-2 xl:grid-cols-3 gap-6">
                {filteredGoals.map((goal: any) => (
