import { useState, useEffect } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Textarea } from "@/components/ui/textarea";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Copy, DollarSign, Gift, Mail, Plus, Share, Trophy, Users } from "lucide-react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { format } from "date-fns";
import Sidebar from "@/components/Sidebar";
import { useUserContext } from "@/context/UserContext";

const referralSchema = z.object({
  referredEmail: z.string().email("Please enter a valid email address"),
  campaignName: z.string().optional(),
});

type ReferralForm = z.infer<typeof referralSchema>;

interface Referral {
  id: string;
  referrerUserId: string;
  referredEmail: string;
  referredUserId?: string;
  referralCode: string;
  status: "pending" | "completed";
  campaignName?: string;
  rewardType: string;
  rewardValue: number;
  completedAt?: string;
  createdAt: string;
  updatedAt: string;
}

interface ReferralReward {
  id: string;
  referralId: string;
  userId: string;
  rewardType: string;
  rewardValue: number;
  rewardDescription: string;
  expiresAt?: string;
  appliedAt?: string;
  appliedToInvoice?: string;
  createdAt: string;
}

interface ReferralStats {
  totalReferrals: number;
  completedReferrals: number;
  pendingReferrals: number;
  totalRewards: number;
  availableRewards: number;
}

export default function ReferralDashboard() {
  const { user } = useUserContext();
  const [showCreateForm, setShowCreateForm] = useState(false);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Redirect if not authenticated
  useEffect(() => {
    if (!user) {
      toast({
        title: "Authentication Required",
        description: "Please log in to access your referral dashboard.",
        variant: "destructive",
      });
      window.location.href = "/api/login";
    }
  }, [user, toast]);

  const form = useForm<ReferralForm>({
    resolver: zodResolver(referralSchema),
    defaultValues: {
      campaignName: "Default Campaign",
    },
  });

  const { data: referralData, isLoading } = useQuery({
    queryKey: ["/api/user/referrals"],
    enabled: !!user,
  });

  const createReferralMutation = useMutation({
    mutationFn: async (data: ReferralForm) => {
      return await apiRequest("POST", "/api/user/referrals", data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/user/referrals"] });
      setShowCreateForm(false);
      form.reset();
      toast({
        title: "Success",
        description: "Referral link created successfully! Share your link to start earning rewards.",
      });
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: error.message || "Failed to create referral",
        variant: "destructive",
      });
    },
  });

  const referrals: Referral[] = (referralData as any)?.referrals || [];
  const stats: ReferralStats = (referralData as any)?.stats || {
    totalReferrals: 0,
    completedReferrals: 0,
    pendingReferrals: 0,
    totalRewards: 0,
    availableRewards: 0,
  };
  const rewards: ReferralReward[] = (referralData as any)?.rewards || [];

  const handleCopyReferralLink = (code: string) => {
    const baseUrl = window.location.origin;
    const referralLink = `${baseUrl}/signup?ref=${code}`;
    navigator.clipboard.writeText(referralLink);
    toast({
      title: "Copied",
      description: "Referral link copied to clipboard",
    });
  };

  const handleCopyCode = (code: string) => {
    navigator.clipboard.writeText(code);
    toast({
      title: "Copied",
      description: "Referral code copied to clipboard",
    });
  };

  const onSubmit = (data: ReferralForm) => {
    createReferralMutation.mutate(data);
  };

  if (!user) {
    return null;
  }

  return (
    <div className="flex min-h-screen bg-background">
      <Sidebar user={user} />
      <div className="flex-1 lg:ml-80">
        <div className="p-8">
          <div className="flex items-center justify-between mb-8">
            <div>
              <h1 className="text-3xl font-bold">Referral Dashboard</h1>
              <p className="text-muted-foreground mt-2">
                Share LVL UP with friends and earn rewards together
              </p>
            </div>

            <Dialog open={showCreateForm} onOpenChange={setShowCreateForm}>
              <DialogTrigger asChild>
                <Button data-testid="button-create-referral">
                  <Plus className="w-4 h-4 mr-2" />
                  Create Referral
                </Button>
              </DialogTrigger>
              <DialogContent>
                <DialogHeader>
                  <DialogTitle>Refer a Friend</DialogTitle>
                  <DialogDescription>
                    Invite someone to join LVL UP and you'll both earn rewards when they sign up
                  </DialogDescription>
                </DialogHeader>

                <Form {...form}>
                  <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                    <FormField
                      control={form.control}
                      name="referredEmail"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Friend's Email</FormLabel>
                          <FormControl>
                            <Input 
                              type="email"
                              placeholder="friend@example.com" 
                              {...field}
                              data-testid="input-friend-email"
                            />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    <FormField
                      control={form.control}
                      name="campaignName"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Campaign Name (Optional)</FormLabel>
                          <FormControl>
                            <Input 
                              placeholder="e.g., Holiday Promotion" 
                              {...field}
                              data-testid="input-campaign-name"
                            />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    <div className="flex justify-end space-x-2">
                      <Button 
                        type="button" 
                        variant="outline" 
                        onClick={() => setShowCreateForm(false)}
                        data-testid="button-cancel"
                      >
                        Cancel
                      </Button>
                      <Button 
                        type="submit" 
                        disabled={createReferralMutation.isPending}
                        data-testid="button-submit"
                      >
                        {createReferralMutation.isPending ? "Creating..." : "Create Referral"}
                      </Button>
                    </div>
                  </form>
                </Form>
              </DialogContent>
            </Dialog>
          </div>

          {/* Stats Overview */}
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-5 gap-6 mb-8">
            <Card>
              <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                <CardTitle className="text-sm font-medium">Total Referrals</CardTitle>
                <Users className="h-4 w-4 text-muted-foreground" />
              </CardHeader>
              <CardContent>
                <div className="text-2xl font-bold" data-testid="text-total-referrals">
                  {stats.totalReferrals}
                </div>
              </CardContent>
            </Card>

            <Card>
              <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                <CardTitle className="text-sm font-medium">Completed</CardTitle>
                <Trophy className="h-4 w-4 text-muted-foreground" />
              </CardHeader>
              <CardContent>
                <div className="text-2xl font-bold text-green-600" data-testid="text-completed-referrals">
                  {stats.completedReferrals}
                </div>
              </CardContent>
            </Card>

            <Card>
              <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                <CardTitle className="text-sm font-medium">Pending</CardTitle>
                <Mail className="h-4 w-4 text-muted-foreground" />
              </CardHeader>
              <CardContent>
                <div className="text-2xl font-bold text-yellow-600" data-testid="text-pending-referrals">
                  {stats.pendingReferrals}
                </div>
              </CardContent>
            </Card>

            <Card>
              <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                <CardTitle className="text-sm font-medium">Total Earned</CardTitle>
                <DollarSign className="h-4 w-4 text-muted-foreground" />
              </CardHeader>
              <CardContent>
                <div className="text-2xl font-bold" data-testid="text-total-rewards">
                  ${(stats.totalRewards / 100).toFixed(2)}
                </div>
              </CardContent>
            </Card>

            <Card>
              <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                <CardTitle className="text-sm font-medium">Available</CardTitle>
                <Gift className="h-4 w-4 text-muted-foreground" />
              </CardHeader>
              <CardContent>
                <div className="text-2xl font-bold text-green-600" data-testid="text-available-rewards">
                  ${(stats.availableRewards / 100).toFixed(2)}
                </div>
              </CardContent>
            </Card>
          </div>

          <Tabs defaultValue="referrals" className="space-y-6">
            <TabsList>
              <TabsTrigger value="referrals">My Referrals</TabsTrigger>
              <TabsTrigger value="rewards">Rewards</TabsTrigger>
            </TabsList>

            <TabsContent value="referrals" className="space-y-6">
              {isLoading ? (
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                  {[...Array(3)].map((_, i) => (
                    <Card key={i} className="h-40 animate-pulse">
                      <CardContent className="p-6">
                        <div className="h-4 bg-muted rounded w-3/4 mb-2"></div>
                        <div className="h-3 bg-muted rounded w-1/2 mb-4"></div>
                        <div className="h-3 bg-muted rounded w-full mb-2"></div>
                        <div className="h-3 bg-muted rounded w-2/3"></div>
                      </CardContent>
                    </Card>
                  ))}
                </div>
              ) : referrals.length > 0 ? (
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                  {referrals.map((referral) => (
                    <Card key={referral.id} className="hover:shadow-md transition-shadow">
                      <CardHeader className="pb-2">
                        <div className="flex items-center justify-between">
                          <CardTitle className="text-lg" data-testid={`text-email-${referral.id}`}>
                            {referral.referredEmail}
                          </CardTitle>
                          <Badge 
                            variant={referral.status === "completed" ? "default" : "secondary"}
                            data-testid={`badge-status-${referral.id}`}
                          >
                            {referral.status}
                          </Badge>
                        </div>
                        <CardDescription data-testid={`text-code-${referral.id}`}>
                          Code: {referral.referralCode}
                        </CardDescription>
                      </CardHeader>
                      <CardContent>
                        <div className="space-y-2 text-sm">
                          <div className="flex justify-between">
                            <span className="text-muted-foreground">Reward:</span>
                            <span className="font-medium" data-testid={`text-reward-${referral.id}`}>
                              ${(referral.rewardValue / 100).toFixed(2)}
                            </span>
                          </div>

                          <div className="flex justify-between">
                            <span className="text-muted-foreground">Created:</span>
                            <span data-testid={`text-created-${referral.id}`}>
                              {format(new Date(referral.createdAt), "MMM dd, yyyy")}
                            </span>
                          </div>

                          {referral.completedAt && (
                            <div className="flex justify-between">
                              <span className="text-muted-foreground">Completed:</span>
                              <span data-testid={`text-completed-${referral.id}`}>
                                {format(new Date(referral.completedAt), "MMM dd, yyyy")}
                              </span>
                            </div>
                          )}

                          {referral.campaignName && (
                            <div className="flex justify-between">
                              <span className="text-muted-foreground">Campaign:</span>
                              <span data-testid={`text-campaign-${referral.id}`}>
                                {referral.campaignName}
                              </span>
                            </div>
                          )}
                        </div>

                        <div className="flex space-x-2 mt-4 pt-4 border-t">
                          <Button
                            variant="outline"
                            size="sm"
                            onClick={() => handleCopyCode(referral.referralCode)}
                            data-testid={`button-copy-code-${referral.id}`}
                          >
                            <Copy className="w-4 h-4 mr-2" />
                            Copy Code
                          </Button>
                          <Button
                            variant="outline"
                            size="sm"
                            onClick={() => handleCopyReferralLink(referral.referralCode)}
                            data-testid={`button-copy-link-${referral.id}`}
                          >
                            <Share className="w-4 h-4 mr-2" />
                            Copy Link
                          </Button>
                        </div>
                      </CardContent>
                    </Card>
                  ))}
                </div>
              ) : (
                <Card className="text-center py-12">
                  <CardContent>
                    <Users className="w-12 h-12 text-muted-foreground mx-auto mb-4" />
                    <h3 className="text-lg font-semibold mb-2">No referrals yet</h3>
                    <p className="text-muted-foreground mb-4">
                      Start referring friends to earn rewards together
                    </p>
                    <Button onClick={() => setShowCreateForm(true)} data-testid="button-create-first">
                      <Plus className="w-4 h-4 mr-2" />
                      Create Your First Referral
                    </Button>
                  </CardContent>
                </Card>
              )}
            </TabsContent>

            <TabsContent value="rewards" className="space-y-6">
              {rewards.length > 0 ? (
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                  {rewards.map((reward) => (
                    <Card key={reward.id} className="hover:shadow-md transition-shadow">
                      <CardHeader className="pb-2">
                        <div className="flex items-center justify-between">
                          <CardTitle className="text-lg" data-testid={`text-reward-type-${reward.id}`}>
                            {reward.rewardType.replace('_', ' ').replace(/\b\w/g, l => l.toUpperCase())}
                          </CardTitle>
                          <Badge 
                            variant={reward.appliedAt ? "secondary" : "default"}
                            data-testid={`badge-reward-status-${reward.id}`}
                          >
                            {reward.appliedAt ? "Applied" : "Available"}
                          </Badge>
                        </div>
                        <CardDescription data-testid={`text-reward-description-${reward.id}`}>
                          {reward.rewardDescription}
                        </CardDescription>
                      </CardHeader>
                      <CardContent>
                        <div className="space-y-2 text-sm">
                          <div className="flex justify-between">
                            <span className="text-muted-foreground">Value:</span>
                            <span className="font-medium text-green-600" data-testid={`text-reward-value-${reward.id}`}>
                              ${(reward.rewardValue / 100).toFixed(2)}
                            </span>
                          </div>

                          <div className="flex justify-between">
                            <span className="text-muted-foreground">Earned:</span>
                            <span data-testid={`text-reward-created-${reward.id}`}>
                              {format(new Date(reward.createdAt), "MMM dd, yyyy")}
                            </span>
                          </div>

                          {reward.expiresAt && (
                            <div className="flex justify-between">
                              <span className="text-muted-foreground">Expires:</span>
                              <span data-testid={`text-reward-expires-${reward.id}`}>
                                {format(new Date(reward.expiresAt), "MMM dd, yyyy")}
                              </span>
                            </div>
                          )}

                          {reward.appliedAt && (
                            <div className="flex justify-between">
                              <span className="text-muted-foreground">Applied:</span>
                              <span data-testid={`text-reward-applied-${reward.id}`}>
                                {format(new Date(reward.appliedAt), "MMM dd, yyyy")}
                              </span>
                            </div>
                          )}
                        </div>

                        {!reward.appliedAt && (
                          <div className="mt-4 pt-4 border-t">
                            <p className="text-xs text-muted-foreground">
                              This reward will be automatically applied to your next invoice
                            </p>
                          </div>
                        )}
                      </CardContent>
                    </Card>
                  ))}
                </div>
              ) : (
                <Card className="text-center py-12">
                  <CardContent>
                    <Gift className="w-12 h-12 text-muted-foreground mx-auto mb-4" />
                    <h3 className="text-lg font-semibold mb-2">No rewards yet</h3>
                    <p className="text-muted-foreground mb-4">
                      Complete referrals to start earning rewards
                    </p>
                  </CardContent>
                </Card>
              )}
            </TabsContent>
          </Tabs>
        </div>
      </div>
    </div>
  );
}

client/src/components/NotificationCenter.content:
import React from 'react';
import { Bell, X, CheckCircle, AlertCircle, Info, Target, Award } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { 
  Popover, 
  PopoverContent, 
  PopoverTrigger 
} from '@/components/ui/popover';
import {
  Card,
  CardContent,
  CardDescription,
  CardHeader,
  CardTitle,
} from '@/components/ui/card';
import { ScrollArea } from '@/components/ui/scroll-area';
import { useWebSocket } from '@/hooks/useWebSocket';
import { formatDistanceToNow } from 'date-fns';

const getNotificationIcon = (type: string) => {
  switch (type) {
    case 'feedback_received':
      return <Award className="w-4 h-4 text-yellow-500" />;
    case 'goal_reminder':
      return <Target className="w-4 h-4 text-blue-500" />;
    case 'performance_review':
      return <CheckCircle className="w-4 h-4 text-green-500" />;
    case 'system_update':
      return <AlertCircle className="w-4 h-4 text-orange-500" />;
    default:
      return <Info className="w-4 h-4 text-gray-500" />;
  }
};

const getNotificationColor = (type: string) => {
  switch (type) {
    case 'feedback_received':
      return 'border-l-yellow-500 bg-yellow-50';
    case 'goal_reminder':
      return 'border-l-blue-500 bg-blue-50';
    case 'performance_review':
      return 'border-l-green-500 bg-green-50';
    case 'system_update':
      return 'border-l-orange-500 bg-orange-50';
    default:
      return 'border-l-gray-500 bg-gray-50';
  }
};

export default function NotificationCenter() {
  const { 
    isConnected, 
    notifications, 
    unreadCount, 
    markNotificationRead, 
    clearNotifications 
  } = useWebSocket();

  return (
    <Popover>
      <PopoverTrigger asChild>
        <Button
          variant="ghost"
          size="sm"
          className="relative"
          data-testid="button-notifications"
        >
          <Bell className="w-5 h-5" />
          {unreadCount > 0 && (
            <Badge 
              variant="destructive" 
              className="absolute -top-2 -right-2 px-1 min-w-[1.2rem] h-5 text-xs"
            >
              {unreadCount > 99 ? '99+' : unreadCount}
            </Badge>
          )}
        </Button>
      </PopoverTrigger>
      <PopoverContent 
        className="w-96 p-0" 
        align="end"
        data-testid="popover-notifications"
      >
        <Card className="border-0 shadow-lg">
          <CardHeader className="pb-3">
            <div className="flex items-center justify-between">
              <div>
                <CardTitle className="text-lg">Notifications</CardTitle>
                <CardDescription className="flex items-center gap-2">
                  <div className={`w-2 h-2 rounded-full ${isConnected ? 'bg-green-500' : 'bg-red-500'}`} />
                  {isConnected ? 'Connected' : 'Disconnected'}
                </CardDescription>
              </div>
              {notifications.length > 0 && (
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={clearNotifications}
                  data-testid="button-clear-notifications"
                >
                  <X className="w-4 h-4" />
                </Button>
              )}
            </div>
          </CardHeader>
          <CardContent className="p-0">
            {notifications.length === 0 ? (
              <div className="p-6 text-center text-muted-foreground">
                <Bell className="w-8 h-8 mx-auto mb-2 opacity-50" />
                <p>No notifications yet</p>
                <p className="text-sm">You'll see updates here when they arrive</p>
              </div>
            ) : (
              <ScrollArea className="h-96">
                <div className="space-y-1 p-2">
                  {notifications.map((notification) => (
                    <div
                      key={notification.id}
                      className={`
                        border-l-4 p-3 rounded-r-lg cursor-pointer
                        transition-all hover:shadow-md
                        ${getNotificationColor(notification.type)}
                        ${notification.isRead ? 'opacity-60' : 'opacity-100'}
                      `}
                      onClick={() => markNotificationRead(notification.id)}
                      data-testid={`notification-${notification.id}`}
                    >
                      <div className="flex items-start gap-3">
                        {getNotificationIcon(notification.type)}
                        <div className="flex-1 min-w-0">
                          <div className="flex items-center justify-between mb-1">
                            <h4 className="font-medium text-sm truncate">
                              {notification.title}
                            </h4>
                            {!notification.isRead && (
                              <div className="w-2 h-2 bg-primary rounded-full flex-shrink-0" />
                            )}
                          </div>
                          <p className="text-sm text-muted-foreground mb-2">
                            {notification.message}
                          </p>
                          <div className="flex items-center justify-between text-xs text-muted-foreground">
                            <span>
                              {formatDistanceToNow(new Date(notification.createdAt), { 
                                addSuffix: true 
                              })}
                            </span>
                            <span className="capitalize">
                              {notification.type.replace('_', ' ')}
                            </span>
                          </div>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              </ScrollArea>
            )}
          </CardContent>
        </Card>
      </PopoverContent>
    </Popover>
  );
}

client/src/hooks/useWebSocket.ts.content:
import { useEffect, useRef, useState, useCallback } from 'react';
import { useUserContext } from '@/context/UserContext';
import { useToast } from './use-toast';

interface WebSocketMessage {
  type: string;
  data?: any;
  message?: string;
}

interface Notification {
  id: string;
  type: string;
  title: string;
  message: string;
  metadata?: any;
  createdAt: string;
  isRead: boolean;
}

export function useWebSocket() {
  const { user, isAuthenticated } = useUserContext();
  const { toast } = useToast();
  const wsRef = useRef<WebSocket | null>(null);
  const [isConnected, setIsConnected] = useState(false);
  const [notifications, setNotifications] = useState<Notification[]>([]);
  const reconnectAttempts = useRef(0);
  const maxReconnectAttempts = 5;

  const connect = useCallback(() => {
    if (!isAuthenticated || !user || wsRef.current?.readyState === WebSocket.OPEN) {
      return;
    }

    try {
      const protocol = window.location.protocol === "https:" ? "wss:" : "ws:";
      const wsUrl = `${protocol}//${window.location.host}/ws`;

      console.log('ðŸ”Œ Connecting to WebSocket:', wsUrl);
      const ws = new WebSocket(wsUrl);
      wsRef.current = ws;

      ws.onopen = () => {
        console.log('âœ… WebSocket connected');
        setIsConnected(true);
        reconnectAttempts.current = 0;

        // Authenticate the connection
        ws.send(JSON.stringify({
          type: 'auth',
          userId: user.id
        }));
      };

      ws.onmessage = (event) => {
        try {
          const message: WebSocketMessage = JSON.parse(event.data);
          console.log('ðŸ“¨ WebSocket message received:', message);

          switch (message.type) {
            case 'auth_success':
              console.log('ðŸ” WebSocket authenticated successfully');
              break;

            case 'notification':
              const notification = message.data as Notification;
              setNotifications(prev => [notification, ...prev]);

              // Show toast notification
              toast({
                title: notification.title,
                description: notification.message,
                variant: notification.type.includes('error') ? 'destructive' : 'default',
              });
              break;

            case 'pong':
              // Heartbeat response
              break;

            case 'error':
              console.error('âŒ WebSocket error:', message.message);
              toast({
                title: 'Connection Error',
                description: message.message || 'WebSocket connection error',
                variant: 'destructive',
              });
              break;

            default:
              console.log('Unknown WebSocket message type:', message.type);
          }
        } catch (error) {
          console.error('âŒ Failed to parse WebSocket message:', error);
        }
      };

      ws.onclose = (event) => {
        console.log('ðŸ”Œ WebSocket disconnected:', event.code, event.reason);
        setIsConnected(false);

        // Auto-reconnect with exponential backoff
        if (reconnectAttempts.current < maxReconnectAttempts) {
          const delay = Math.pow(2, reconnectAttempts.current) * 1000; // 1s, 2s, 4s, 8s, 16s
          console.log(`ðŸ”„ Reconnecting in ${delay}ms... (attempt ${reconnectAttempts.current + 1}/${maxReconnectAttempts})`);

          setTimeout(() => {
            reconnectAttempts.current++;
            connect();
          }, delay);
        } else {
          console.error('âŒ Max reconnection attempts reached');
          toast({
            title: 'Connection Lost',
            description: 'Unable to maintain real-time connection. Please refresh the page.',
            variant: 'destructive',
          });
        }
      };

      ws.onerror = (error) => {
        console.error('âŒ WebSocket error:', error);
      };

    } catch (error) {
      console.error('âŒ Failed to create WebSocket connection:', error);
    }
  }, [isAuthenticated, user, toast]);

  const disconnect = useCallback(() => {
    if (wsRef.current) {
      wsRef.current.close(1000, 'Manual disconnect');
      wsRef.current = null;
      setIsConnected(false);
    }
  }, []);

  const sendMessage = useCallback((message: any) => {
    if (wsRef.current?.readyState === WebSocket.OPEN) {
      wsRef.current.send(JSON.stringify(message));
      return true;
    }
    return false;
  }, []);

  const markNotificationRead = useCallback((notificationId: string) => {
    setNotifications(prev => 
      prev.map(notification => 
        notification.id === notificationId 
          ? { ...notification, isRead: true }
          : notification
      )
    );
  }, []);

  const clearNotifications = useCallback(() => {
    setNotifications([]);
  }, []);

  // Effect to handle connection
  useEffect(() => {
    if (isAuthenticated && user) {
      connect();
    } else {
      disconnect();
    }

    return () => {
      disconnect();
    };
  }, [isAuthenticated, user, connect, disconnect]);

  // Heartbeat to keep connection alive
  useEffect(() => {
    if (!isConnected) return;

    const interval = setInterval(() => {
      sendMessage({ type: 'ping' });
    }, 30000); // Ping every 30 seconds

    return () => clearInterval(interval);
  }, [isConnected, sendMessage]);

  return {
    isConnected,
    notifications,
    unreadCount: notifications.filter(n => !n.isRead).length,
    markNotificationRead,
    clearNotifications,
    sendMessage,
    connect,
    disconnect
  };
}

client/src/components/AdvancedEmployeeMa.content:
import { useState } from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from '@/components/ui/dialog';
import { Avatar, AvatarFallback, AvatarImage } from '@/components/ui/avatar';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { useToast } from '@/hooks/use-toast';
import { apiRequest } from '@/lib/queryClient';
import { 
  Users, Plus, Search, Filter, UserPlus, Building2, Briefcase, 
  ChevronDown, MoreHorizontal, Edit, Trash2, UserCheck, Users2,
  MapPin, Star, Calendar, Phone, Mail, Globe, Settings
} from 'lucide-react';

interface Employee {
  id: string;
  userId: string;
  tenantId: string;
  employeeNumber?: string;
  jobPositionId?: string;
  departmentId?: string;
  managerId?: string;
  feedbackUrl: string;
  hireDate?: string;
  status: string;
  bio?: string;
  skills?: string[];
  workLocation?: string;
  emergencyContact?: any;
  personalGoals?: string;
  achievements?: any;
  salaryGrade?: string;
  performanceRating?: number;
  tags?: string[];
  firstName: string;
  lastName: string;
  email: string;
  role: string;
  profileImageUrl?: string;
  phoneNumber?: string;
}

interface Department {
  id: string;
  name: string;
  description?: string;
  parentDepartmentId?: string;
}

interface JobPosition {
  id: string;
  title: string;
  department?: string;
  level?: number;
  description?: string;
}

interface AdvancedEmployeeManagementProps {
  user: any;
}

export default function AdvancedEmployeeManagement({ user }: AdvancedEmployeeManagementProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedDepartment, setSelectedDepartment] = useState<string>('');
  const [selectedStatus, setSelectedStatus] = useState<string>('');
  const [selectedEmployees, setSelectedEmployees] = useState<string[]>([]);
  const [isCreateEmployeeOpen, setIsCreateEmployeeOpen] = useState(false);
  const [isCreateDepartmentOpen, setIsCreateDepartmentOpen] = useState(false);
  const [isCreateJobPositionOpen, setIsCreateJobPositionOpen] = useState(false);
  const [isBulkActionsOpen, setIsBulkActionsOpen] = useState(false);
  const [activeTab, setActiveTab] = useState('employees');

  // Fetch data
  const { data: employees = [], isLoading: employeesLoading } = useQuery<Employee[]>({
    queryKey: ['/api/employees', user?.tenantId],
    enabled: !!user?.tenantId,
  });

  const { data: departments = [] } = useQuery<Department[]>({
    queryKey: ['/api/departments', user?.tenantId],
    enabled: !!user?.tenantId,
  });

  const { data: jobPositions = [] } = useQuery<JobPosition[]>({
    queryKey: ['/api/job-positions', user?.tenantId],
    enabled: !!user?.tenantId,
  });

  // Filtered employees
  const filteredEmployees = employees.filter(emp => {
    const matchesSearch = !searchQuery || 
      emp.firstName?.toLowerCase().includes(searchQuery.toLowerCase()) ||
      emp.lastName?.toLowerCase().includes(searchQuery.toLowerCase()) ||
      emp.email?.toLowerCase().includes(searchQuery.toLowerCase()) ||
      emp.employeeNumber?.toLowerCase().includes(searchQuery.toLowerCase());

    const matchesDepartment = !selectedDepartment || emp.departmentId === selectedDepartment;
    const matchesStatus = !selectedStatus || emp.status === selectedStatus;

    return matchesSearch && matchesDepartment && matchesStatus;
  });

  // Create employee mutation
  const createEmployeeMutation = useMutation({
    mutationFn: async (data: any) => {
      return apiRequest('POST', '/api/employees', data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/employees'] });
      toast({ title: 'Success', description: 'Employee created successfully!' });
      setIsCreateEmployeeOpen(false);
    },
    onError: (error: any) => {
      toast({ 
        title: 'Error', 
        description: error.message || 'Failed to create employee',
        variant: 'destructive' 
      });
    }
  });

  // Create department mutation
  const createDepartmentMutation = useMutation({
    mutationFn: async (data: any) => {
      return apiRequest('POST', '/api/departments', { ...data, tenantId: user.tenantId });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/departments'] });
      toast({ title: 'Success', description: 'Department created successfully!' });
      setIsCreateDepartmentOpen(false);
    },
    onError: (error: any) => {
      toast({ 
        title: 'Error', 
        description: error.message || 'Failed to create department',
        variant: 'destructive' 
      });
    }
  });

  // Create job position mutation
  const createJobPositionMutation = useMutation({
    mutationFn: async (data: any) => {
      return apiRequest('POST', '/api/job-positions', { ...data, tenantId: user.tenantId });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/job-positions'] });
      toast({ title: 'Success', description: 'Job position created successfully!' });
      setIsCreateJobPositionOpen(false);
    },
    onError: (error: any) => {
      toast({ 
        title: 'Error', 
        description: error.message || 'Failed to create job position',
        variant: 'destructive' 
      });
    }
  });

  // Bulk operations mutation
  const bulkAssignMutation = useMutation({
    mutationFn: async ({ type, value }: { type: 'department' | 'manager'; value: string }) => {
      const endpoint = type === 'department' 
        ? '/api/employees/bulk-assign-department' 
        : '/api/employees/bulk-assign-manager';
      return apiRequest('POST', endpoint, { 
        employeeIds: selectedEmployees, 
        [type + 'Id']: value 
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/employees'] });
      toast({ title: 'Success', description: 'Bulk operation completed successfully!' });
      setSelectedEmployees([]);
      setIsBulkActionsOpen(false);
    },
    onError: (error: any) => {
      toast({ 
        title: 'Error', 
        description: error.message || 'Bulk operation failed',
        variant: 'destructive' 
      });
    }
  });

  const handleSelectEmployee = (employeeId: string, checked: boolean) => {
    if (checked) {
      setSelectedEmployees([...selectedEmployees, employeeId]);
    } else {
      setSelectedEmployees(selectedEmployees.filter(id => id !== employeeId));
    }
  };

  const handleSelectAll = (checked: boolean) => {
    if (checked) {
      setSelectedEmployees(filteredEmployees.map(emp => emp.id));
    } else {
      setSelectedEmployees([]);
    }
  };

  const getDepartmentName = (departmentId?: string) => {
    if (!departmentId) return 'Unassigned';
    return departments.find(dept => dept.id === departmentId)?.name || 'Unknown';
  };

  const getJobPositionTitle = (jobPositionId?: string) => {
    if (!jobPositionId) return 'Unassigned';
    return jobPositions.find(pos => pos.id === jobPositionId)?.title || 'Unknown';
  };

  const getManagerName = (managerId?: string) => {
    if (!managerId) return 'No Manager';
    const manager = employees.find(emp => emp.id === managerId);
    return manager ? `${manager.firstName} ${manager.lastName}` : 'Unknown';
  };

  return (
    <div className="space-y-6">
      {/* Header with Actions */}
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-2xl font-bold gradient-text">Advanced Employee Management</h2>
          <p className="text-sm text-muted-foreground">
            Comprehensive employee, department, and organizational management
          </p>
        </div>
        <div className="flex items-center space-x-2">
          <Dialog open={isCreateDepartmentOpen} onOpenChange={setIsCreateDepartmentOpen}>
            <DialogTrigger asChild>
              <Button variant="outline" data-testid="button-create-department">
                <Building2 className="w-4 h-4 mr-2" />
                Add Department
              </Button>
            </DialogTrigger>
            <DialogContent>
              <DialogHeader>
                <DialogTitle>Create Department</DialogTitle>
              </DialogHeader>
              <CreateDepartmentForm 
                departments={departments}
                onSubmit={(data) => createDepartmentMutation.mutate(data)}
                isLoading={createDepartmentMutation.isPending}
              />
            </DialogContent>
          </Dialog>

          <Dialog open={isCreateJobPositionOpen} onOpenChange={setIsCreateJobPositionOpen}>
            <DialogTrigger asChild>
              <Button variant="outline" data-testid="button-create-job-position">
                <Briefcase className="w-4 h-4 mr-2" />
                Add Position
              </Button>
            </DialogTrigger>
            <DialogContent>
              <DialogHeader>
                <DialogTitle>Create Job Position</DialogTitle>
              </DialogHeader>
              <CreateJobPositionForm 
                onSubmit={(data) => createJobPositionMutation.mutate(data)}
                isLoading={createJobPositionMutation.isPending}
              />
            </DialogContent>
          </Dialog>

          <Dialog open={isCreateEmployeeOpen} onOpenChange={setIsCreateEmployeeOpen}>
            <DialogTrigger asChild>
              <Button className="bg-primary hover:bg-primary/90" data-testid="button-create-employee">
                <UserPlus className="w-4 h-4 mr-2" />
                Add Employee
              </Button>
            </DialogTrigger>
            <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto">
              <DialogHeader>
                <DialogTitle>Create Employee</DialogTitle>
              </DialogHeader>
              <CreateEmployeeForm 
                departments={departments}
                jobPositions={jobPositions}
                employees={employees}
                onSubmit={(data) => createEmployeeMutation.mutate({ ...data, tenantId: user.tenantId })}
                isLoading={createEmployeeMutation.isPending}
              />
            </DialogContent>
          </Dialog>
        </div>
      </div>

      {/* Search and Filters */}
      <Card className="glass-card border-0">
        <CardContent className="p-6">
          <div className="flex flex-wrap items-center gap-4">
            <div className="flex-1 min-w-[300px]">
              <div className="relative">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-muted-foreground w-4 h-4" />
                <Input
                  placeholder="Search employees by name, email, or employee number..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  className="pl-10"
                  data-testid="input-search-employees"
                />
              </div>
            </div>

            <Select value={selectedDepartment} onValueChange={setSelectedDepartment}>
              <SelectTrigger className="w-[200px]" data-testid="select-department-filter">
                <SelectValue placeholder="All Departments" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="">All Departments</SelectItem>
                {departments.map(dept => (
                  <SelectItem key={dept.id} value={dept.id}>{dept.name}</SelectItem>
                ))}
              </SelectContent>
            </Select>

            <Select value={selectedStatus} onValueChange={setSelectedStatus}>
              <SelectTrigger className="w-[150px]" data-testid="select-status-filter">
                <SelectValue placeholder="All Status" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="">All Status</SelectItem>
                <SelectItem value="active">Active</SelectItem>
                <SelectItem value="inactive">Inactive</SelectItem>
                <SelectItem value="terminated">Terminated</SelectItem>
              </SelectContent>
            </Select>

            {selectedEmployees.length > 0 && (
              <Dialog open={isBulkActionsOpen} onOpenChange={setIsBulkActionsOpen}>
                <DialogTrigger asChild>
                  <Button variant="outline" data-testid="button-bulk-actions">
                    <Users2 className="w-4 h-4 mr-2" />
                    Bulk Actions ({selectedEmployees.length})
                  </Button>
                </DialogTrigger>
                <DialogContent>
                  <DialogHeader>
                    <DialogTitle>Bulk Operations</DialogTitle>
                  </DialogHeader>
                  <BulkActionsForm 
                    departments={departments}
                    employees={employees}
                    selectedCount={selectedEmployees.length}
                    onSubmit={(data) => bulkAssignMutation.mutate(data)}
                    isLoading={bulkAssignMutation.isPending}
                  />
                </DialogContent>
              </Dialog>
            )}
          </div>
        </CardContent>
      </Card>

      {/* Tabs for different views */}
      <Tabs value={activeTab} onValueChange={setActiveTab} className="space-y-4">
        <TabsList className="grid w-full grid-cols-3">
          <TabsTrigger value="employees" data-testid="tab-employees">Employees ({filteredEmployees.length})</TabsTrigger>
          <TabsTrigger value="departments" data-testid="tab-departments">Departments ({departments.length})</TabsTrigger>
          <TabsTrigger value="positions" data-testid="tab-positions">Positions ({jobPositions.length})</TabsTrigger>
        </TabsList>

        <TabsContent value="employees" className="space-y-4">
          {employeesLoading ? (
            <EmployeeSkeletonLoader />
          ) : (
            <EmployeeGrid 
              employees={filteredEmployees}
              selectedEmployees={selectedEmployees}
              onSelectEmployee={handleSelectEmployee}
              onSelectAll={handleSelectAll}
              getDepartmentName={getDepartmentName}
              getJobPositionTitle={getJobPositionTitle}
              getManagerName={getManagerName}
            />
          )}
        </TabsContent>

        <TabsContent value="departments" className="space-y-4">
          <DepartmentGrid departments={departments} employees={employees} />
        </TabsContent>

        <TabsContent value="positions" className="space-y-4">
          <JobPositionGrid jobPositions={jobPositions} employees={employees} />
        </TabsContent>
      </Tabs>
    </div>
  );
}

// Sub-components for different forms and views
function CreateEmployeeForm({ 
  departments, 
  jobPositions, 
  employees, 
  onSubmit, 
  isLoading 
}: any) {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    phoneNumber: '',
    employeeNumber: '',
    departmentId: '',
    jobPositionId: '',
    managerId: '',
    workLocation: 'office',
    bio: '',
    skills: '',
    personalGoals: ''
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    onSubmit({
      ...formData,
      skills: formData.skills.split(',').map(s => s.trim()).filter(Boolean)
    });
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-4">
      <div className="grid grid-cols-2 gap-4">
        <div>
          <Label htmlFor="firstName">First Name *</Label>
          <Input
            id="firstName"
            value={formData.firstName}
            onChange={(e) => setFormData({...formData, firstName: e.target.value})}
            required
            data-testid="input-employee-first-name"
          />
        </div>
        <div>
          <Label htmlFor="lastName">Last Name *</Label>
          <Input
            id="lastName"
            value={formData.lastName}
            onChange={(e) => setFormData({...formData, lastName: e.target.value})}
            required
            data-testid="input-employee-last-name"
          />
        </div>
      </div>

      <div className="grid grid-cols-2 gap-4">
        <div>
          <Label htmlFor="email">Email *</Label>
          <Input
            id="email"
            type="email"
            value={formData.email}
            onChange={(e) => setFormData({...formData, email: e.target.value})}
            required
            data-testid="input-employee-email"
          />
        </div>
        <div>
          <Label htmlFor="phoneNumber">Phone Number</Label>
          <Input
            id="phoneNumber"
            value={formData.phoneNumber}
            onChange={(e) => setFormData({...formData, phoneNumber: e.target.value})}
            data-testid="input-employee-phone"
          />
        </div>
      </div>

      <div className="grid grid-cols-2 gap-4">
        <div>
          <Label htmlFor="employeeNumber">Employee Number</Label>
          <Input
            id="employeeNumber"
            value={formData.employeeNumber}
            onChange={(e) => setFormData({...formData, employeeNumber: e.target.value})}
            data-testid="input-employee-number"
          />
        </div>
        <div>
          <Label htmlFor="workLocation">Work Location</Label>
          <Select value={formData.workLocation} onValueChange={(value) => setFormData({...formData, workLocation: value})}>
            <SelectTrigger data-testid="select-work-location">
              <SelectValue />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="office">Office</SelectItem>
              <SelectItem value="remote">Remote</SelectItem>
              <SelectItem value="hybrid">Hybrid</SelectItem>
            </SelectContent>
          </Select>
        </div>
      </div>

      <div className="grid grid-cols-2 gap-4">
        <div>
          <Label htmlFor="departmentId">Department</Label>
          <Select value={formData.departmentId} onValueChange={(value) => setFormData({...formData, departmentId: value})}>
            <SelectTrigger data-testid="select-employee-department">
              <SelectValue placeholder="Select Department" />
            </SelectTrigger>
            <SelectContent>
              {departments.map((dept: Department) => (
                <SelectItem key={dept.id} value={dept.id}>{dept.name}</SelectItem>
              ))}
            </SelectContent>
          </Select>
        </div>
        <div>
          <Label htmlFor="jobPositionId">Job Position</Label>
          <Select value={formData.jobPositionId} onValueChange={(value) => setFormData({...formData, jobPositionId: value})}>
            <SelectTrigger data-testid="select-employee-position">
              <SelectValue placeholder="Select Position" />
            </SelectTrigger>
            <SelectContent>
              {jobPositions.map((pos: JobPosition) => (
                <SelectItem key={pos.id} value={pos.id}>{pos.title}</SelectItem>
              ))}
            </SelectContent>
          </Select>
        </div>
      </div>

      <div>
        <Label htmlFor="managerId">Manager</Label>
        <Select value={formData.managerId} onValueChange={(value) => setFormData({...formData, managerId: value})}>
          <SelectTrigger data-testid="select-employee-manager">
            <SelectValue placeholder="Select Manager" />
          </SelectTrigger>
          <SelectContent>
            {employees.filter((emp: Employee) => emp.role === 'manager' || emp.role === 'tenant_admin').map((emp: Employee) => (
              <SelectItem key={emp.id} value={emp.id}>{emp.firstName} {emp.lastName}</SelectItem>
            ))}
          </SelectContent>
        </Select>
      </div>

      <div>
        <Label htmlFor="bio">Bio</Label>
        <Textarea
          id="bio"
          value={formData.bio}
          onChange={(e) => setFormData({...formData, bio: e.target.value})}
          placeholder="Brief description of the employee..."
          data-testid="textarea-employee-bio"
        />
      </div>

      <div>
        <Label htmlFor="skills">Skills (comma-separated)</Label>
        <Input
          id="skills"
          value={formData.skills}
          onChange={(e) => setFormData({...formData, skills: e.target.value})}
          placeholder="JavaScript, Project Management, Design..."
          data-testid="input-employee-skills"
        />
      </div>

      <div>
        <Label htmlFor="personalGoals">Personal Goals</Label>
        <Textarea
          id="personalGoals"
          value={formData.personalGoals}
          onChange={(e) => setFormData({...formData, personalGoals: e.target.value})}
          placeholder="Professional development goals and aspirations..."
          data-testid="textarea-employee-goals"
        />
      </div>

      <div className="flex justify-end space-x-2 pt-4">
        <Button type="submit" disabled={isLoading} data-testid="button-submit-employee">
          {isLoading ? 'Creating...' : 'Create Employee'}
        </Button>
      </div>
    </form>
  );
}

function CreateDepartmentForm({ departments, onSubmit, isLoading }: any) {
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    parentDepartmentId: ''
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    onSubmit(formData);
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-4">
      <div>
        <Label htmlFor="name">Department Name *</Label>
        <Input
          id="name"
          value={formData.name}
          onChange={(e) => setFormData({...formData, name: e.target.value})}
          required
          data-testid="input-department-name"
        />
      </div>

      <div>
        <Label htmlFor="description">Description</Label>
        <Textarea
          id="description"
          value={formData.description}
          onChange={(e) => setFormData({...formData, description: e.target.value})}
          placeholder="Brief description of the department..."
          data-testid="textarea-department-description"
        />
      </div>

      <div>
        <Label htmlFor="parentDepartmentId">Parent Department</Label>
        <Select value={formData.parentDepartmentId} onValueChange={(value) => setFormData({...formData, parentDepartmentId: value})}>
          <SelectTrigger data-testid="select-parent-department">
            <SelectValue placeholder="Select Parent Department (Optional)" />
          </SelectTrigger>
          <SelectContent>
            {departments.map((dept: Department) => (
              <SelectItem key={dept.id} value={dept.id}>{dept.name}</SelectItem>
            ))}
          </SelectContent>
        </Select>
      </div>

      <div className="flex justify-end space-x-2 pt-4">
        <Button type="submit" disabled={isLoading} data-testid="button-submit-department">
          {isLoading ? 'Creating...' : 'Create Department'}
        </Button>
      </div>
    </form>
  );
}

function CreateJobPositionForm({ onSubmit, isLoading }: any) {
  const [formData, setFormData] = useState({
    title: '',
    department: '',
    level: 1,
    description: ''
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    onSubmit(formData);
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-4">
      <div>
        <Label htmlFor="title">Position Title *</Label>
        <Input
          id="title"
          value={formData.title}
          onChange={(e) => setFormData({...formData, title: e.target.value})}
          required
          data-testid="input-position-title"
        />
      </div>

      <div>
        <Label htmlFor="department">Department</Label>
        <Input
          id="department"
          value={formData.department}
          onChange={(e) => setFormData({...formData, department: e.target.value})}
          data-testid="input-position-department"
        />
      </div>

      <div>
        <Label htmlFor="level">Level (1-10)</Label>
        <Input
          id="level"
          type="number"
          min="1"
          max="10"
          value={formData.level}
          onChange={(e) => setFormData({...formData, level: parseInt(e.target.value)})}
          data-testid="input-position-level"
        />
      </div>

      <div>
        <Label htmlFor="description">Description</Label>
        <Textarea
          id="description"
          value={formData.description}
          onChange={(e) => setFormData({...formData, description: e.target.value})}
          placeholder="Job responsibilities and requirements..."
          data-testid="textarea-position-description"
        />
      </div>

      <div className="flex justify-end space-x-2 pt-4">
        <Button type="submit" disabled={isLoading} data-testid="button-submit-position">
          {isLoading ? 'Creating...' : 'Create Position'}
        </Button>
      </div>
    </form>
  );
}

function BulkActionsForm({ departments, employees, selectedCount, onSubmit, isLoading }: any) {
  const [action, setAction] = useState<'department' | 'manager'>('department');
  const [targetValue, setTargetValue] = useState('');

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (targetValue) {
      onSubmit({ type: action, value: targetValue });
    }
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-4">
      <div>
        <p className="text-sm text-muted-foreground mb-4">
          Performing bulk operation on {selectedCount} selected employees
        </p>
      </div>

      <div>
        <Label>Action Type</Label>
        <Select value={action} onValueChange={(value: 'department' | 'manager') => setAction(value)}>
          <SelectTrigger data-testid="select-bulk-action-type">
            <SelectValue />
          </SelectTrigger>
          <SelectContent>
            <SelectItem value="department">Assign Department</SelectItem>
            <SelectItem value="manager">Assign Manager</SelectItem>
          </SelectContent>
        </Select>
      </div>

      <div>
        <Label>
          {action === 'department' ? 'Select Department' : 'Select Manager'}
        </Label>
        <Select value={targetValue} onValueChange={setTargetValue}>
          <SelectTrigger data-testid="select-bulk-target">
            <SelectValue placeholder={`Select ${action === 'department' ? 'Department' : 'Manager'}`} />
          </SelectTrigger>
          <SelectContent>
            {action === 'department' 
              ? departments.map((dept: Department) => (
                  <SelectItem key={dept.id} value={dept.id}>{dept.name}</SelectItem>
                ))
              : employees.filter((emp: Employee) => emp.role === 'manager' || emp.role === 'tenant_admin').map((emp: Employee) => (
                  <SelectItem key={emp.id} value={emp.id}>{emp.firstName} {emp.lastName}</SelectItem>
                ))
            }
          </SelectContent>
        </Select>
      </div>

      <div className="flex justify-end space-x-2 pt-4">
        <Button type="submit" disabled={!targetValue || isLoading} data-testid="button-submit-bulk-action">
          {isLoading ? 'Processing...' : `Update ${selectedCount} Employees`}
        </Button>
      </div>
    </form>
  );
}

function EmployeeGrid({ 
  employees, 
  selectedEmployees, 
  onSelectEmployee, 
  onSelectAll, 
  getDepartmentName, 
  getJobPositionTitle, 
  getManagerName 
}: any) {
  return (
    <Card className="glass-card border-0">
      <CardHeader>
        <div className="flex items-center justify-between">
          <CardTitle>Employee Directory</CardTitle>
          <div className="flex items-center space-x-2">
            <input
              type="checkbox"
              checked={selectedEmployees.length === employees.length && employees.length > 0}
              onChange={(e) => onSelectAll(e.target.checked)}
              className="rounded"
              data-testid="checkbox-select-all"
            />
            <Label className="text-sm">Select All</Label>
          </div>
        </div>
      </CardHeader>
      <CardContent>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {employees.map((employee: Employee) => (
            <div 
              key={employee.id} 
              className={`relative p-6 rounded-lg border transition-all hover:shadow-md ${
                selectedEmployees.includes(employee.id) 
                  ? 'bg-blue-50 border-blue-200 dark:bg-blue-950 dark:border-blue-800' 
                  : 'bg-background border-border'
              }`}
              data-testid={`employee-card-${employee.id}`}
            >
              <div className="absolute top-4 right-4">
                <input
                  type="checkbox"
                  checked={selectedEmployees.includes(employee.id)}
                  onChange={(e) => onSelectEmployee(employee.id, e.target.checked)}
                  className="rounded"
                  data-testid={`checkbox-employee-${employee.id}`}
                />
              </div>

              <div className="flex items-start space-x-4">
                <Avatar className="w-12 h-12">
                  <AvatarImage src={employee.profileImageUrl} />
                  <AvatarFallback>
                    {employee.firstName?.charAt(0)}{employee.lastName?.charAt(0)}
                  </AvatarFallback>
                </Avatar>

                <div className="flex-1 min-w-0">
                  <h3 className="font-semibold text-lg truncate" data-testid={`employee-name-${employee.id}`}>
                    {employee.firstName} {employee.lastName}
                  </h3>
                  <p className="text-sm text-muted-foreground truncate">{employee.email}</p>

                  <div className="mt-3 space-y-2">
                    <div className="flex items-center text-sm">
                      <Building2 className="w-4 h-4 mr-2 text-muted-foreground" />
                      <span>{getDepartmentName(employee.departmentId)}</span>
                    </div>

                    <div className="flex items-center text-sm">
                      <Briefcase className="w-4 h-4 mr-2 text-muted-foreground" />
                      <span>{getJobPositionTitle(employee.jobPositionId)}</span>
                    </div>

                    <div className="flex items-center text-sm">
                      <Users className="w-4 h-4 mr-2 text-muted-foreground" />
                      <span>{getManagerName(employee.managerId)}</span>
                    </div>

                    {employee.workLocation && (
                      <div className="flex items-center text-sm">
                        <MapPin className="w-4 h-4 mr-2 text-muted-foreground" />
                        <span className="capitalize">{employee.workLocation}</span>
                      </div>
                    )}
                  </div>

                  <div className="mt-4 flex items-center justify-between">
                    <Badge 
                      variant={employee.status === 'active' ? 'default' : 'secondary'}
                      data-testid={`employee-status-${employee.id}`}
                    >
                      {employee.status}
                    </Badge>

                    {employee.performanceRating && (
                      <div className="flex items-center text-sm">
                        <Star className="w-4 h-4 mr-1 text-yellow-500" />
                        <span>{employee.performanceRating}</span>
                      </div>
                    )}
                  </div>

                  {employee.skills && employee.skills.length > 0 && (
                    <div className="mt-3">
                      <div className="flex flex-wrap gap-1">
                        {employee.skills.slice(0, 3).map((skill, index) => (
                          <Badge key={index} variant="outline" className="text-xs">
                            {skill}
                          </Badge>
                        ))}
                        {employee.skills.length > 3 && (
                          <Badge variant="outline" className="text-xs">
                            +{employee.skills.length - 3} more
                          </Badge>
                        )}
                      </div>
                    </div>
                  )}
                </div>
              </div>
            </div>
          ))}
        </div>
      </CardContent>
    </Card>
  );
}

function DepartmentGrid({ departments, employees }: any) {
  return (
    <Card className="glass-card border-0">
      <CardHeader>
        <CardTitle>Departments</CardTitle>
      </CardHeader>
      <CardContent>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {departments.map((department: Department) => {
            const deptEmployees = employees.filter((emp: Employee) => emp.departmentId === department.id);
            return (
              <div key={department.id} className="p-6 rounded-lg border bg-background" data-testid={`department-card-${department.id}`}>
                <div className="flex items-start justify-between">
                  <div className="flex-1">
                    <h3 className="font-semibold text-lg">{department.name}</h3>
                    {department.description && (
                      <p className="text-sm text-muted-foreground mt-1">{department.description}</p>
                    )}
                  </div>
                  <Button variant="ghost" size="sm">
                    <MoreHorizontal className="w-4 h-4" />
                  </Button>
                </div>

                <div className="mt-4 flex items-center justify-between">
                  <div className="flex items-center text-sm text-muted-foreground">
                    <Users className="w-4 h-4 mr-2" />
                    <span>{deptEmployees.length} employees</span>
                  </div>
                  <Badge variant="outline">{deptEmployees.length > 10 ? 'Large' : deptEmployees.length > 5 ? 'Medium' : 'Small'}</Badge>
                </div>
              </div>
            );
          })}
        </div>
      </CardContent>
    </Card>
  );
}

function JobPositionGrid({ jobPositions, employees }: any) {
  return (
    <Card className="glass-card border-0">
      <CardHeader>
        <CardTitle>Job Positions</CardTitle>
      </CardHeader>
      <CardContent>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {jobPositions.map((position: JobPosition) => {
            const posEmployees = employees.filter((emp: Employee) => emp.jobPositionId === position.id);
            return (
              <div key={position.id} className="p-6 rounded-lg border bg-background" data-testid={`position-card-${position.id}`}>
                <div className="flex items-start justify-between">
                  <div className="flex-1">
                    <h3 className="font-semibold text-lg">{position.title}</h3>
                    {position.department && (
                      <p className="text-sm text-muted-foreground">{position.department}</p>
                    )}
                    {position.description && (
                      <p className="text-sm text-muted-foreground mt-1">{position.description}</p>
                    )}
                  </div>
                  <Button variant="ghost" size="sm">
                    <MoreHorizontal className="w-4 h-4" />
                  </Button>
                </div>

                <div className="mt-4 flex items-center justify-between">
                  <div className="flex items-center text-sm text-muted-foreground">
                    <Users className="w-4 h-4 mr-2" />
                    <span>{posEmployees.length} employees</span>
                  </div>
                  {position.level && (
                    <Badge variant="outline">Level {position.level}</Badge>
                  )}
                </div>
              </div>
            );
          })}
        </div>
      </CardContent>
    </Card>
  );
}

function EmployeeSkeletonLoader() {
  return (
    <Card className="glass-card border-0">
      <CardHeader>
        <CardTitle>Loading Employees...</CardTitle>
      </CardHeader>
      <CardContent>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {[1, 2, 3, 4, 5, 6].map(i => (
            <div key={i} className="p-6 rounded-lg border bg-background animate-pulse">
              <div className="flex items-start space-x-4">
                <div className="w-12 h-12 bg-gray-200 rounded-full"></div>
                <div className="flex-1 space-y-2">
                  <div className="h-4 bg-gray-200 rounded w-3/4"></div>
                  <div className="h-3 bg-gray-200 rounded w-1/2"></div>
                  <div className="h-3 bg-gray-200 rounded w-2/3"></div>
                </div>
              </div>
            </div>
          ))}
        </div>
      </CardContent>
    </Card>
  );
}

